CREATE TABLE `gurteltier`.`productos` ( `ID` INT NOT NULL AUTO_INCREMENT , `ID_F` VARCHAR(50) NOT NULL , `ID_D` VARCHAR(50) NOT NULL , `V` TEXT NOT NULL , `ID_S` INT NOT NULL , `TIMESTAMP` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , INDEX (`ID_F`), INDEX (`ID_D`), UNIQUE `ID` (`ID`)) ENGINE = InnoDB;

CREATE TABLE `gurteltier`.`CAT_D` ( `ID` INT NOT NULL AUTO_INCREMENT , `NAME` VARCHAR(250) NOT NULL , PRIMARY KEY (`ID`), UNIQUE `NAME` (`NAME`)) ENGINE = MyISAM;